mapper.update:
update comments
set CONTENT=#{bContent}
where COM_NO=#{comNo}

update board
set COM_COUNT = COM_COUNT - 1
where board_no = #{boardNo}

mapper.select:
select count(*)
from support
where BOARD_NO = #{bNo} and SEND_ID = #{sendId}

select count(*)
from bookmark
where BOARD_NO = #{boardNo} and USER_ID = #{userId}
