mapper.update:
update board
set B_COUNT = B_COUNT + 1
where BOARD_NO=#{BOARD_NO}

update board
set B_TITLE=#{bTitle}, B_CONTENT=#{bContent}, CATE=#{cate}, UPDATE_DAY=sysdate
where BOARD_NO=#{boardNo}

update board
set B_STATUS='N'
where BOARD_NO=#{boardNo}

update board
set b_count = b_count + 1
where board_no = #{boardNo}
