mapper.select:
select count(*)
from member
where user_id = #{id}

select count(*)
from member
where EMAIL = #{email}

mapper.update:
UPDATE member 
SET pwd = #{key} 
WHERE user_id = #{user_id}

UPDATE member 
SET pwd = #{newPwd} 
WHERE user_id = #{id}

UPDATE member SET PWD = #{ pwd } , MNAME = #{ name },
				  GENDER = #{ gender },BIRTHDAY = #{ birthDay },EMAIL = #{ email },PHONE = #{ phone },ADDRESS = #{ address }
				  , MBTI = #{ mbti }

WHERE user_id = #{ id }

update member
set status = 'N'
where user_id = #{id}

update member
set status = 'Y'
where user_id = #{id}

UPDATE member 
SET MPOINT = MPOINT + #{point} 
WHERE user_id = #{id}

mapper.select:
select MPOINT
from member
where user_id = #{id}

mapper.update:
UPDATE member 
SET MRANK = #{rank} 
WHERE user_id = #{id}

UPDATE member 
SET MPOINT = MPOINT - #{point} 
WHERE user_id = #{id}

UPDATE MEMBER
SET MPOINT = MPOINT - #{point} 
WHERE user_id = #{id}
