mapper.select:
select count(*)
from member
where user_id = #{id}

select count(*)
from member
where EMAIL = #{email}

mapper.update:
UPDATE member 
SET pwd = #{key} 
WHERE user_id = #{user_id}

UPDATE member 
SET pwd = #{newPwd} 
WHERE user_id = #{id}

UPDATE member SET PWD = #{ pwd } , MNAME = #{ name },
				  GENDER = #{ gender },BIRTHDAY = #{ birthDay },EMAIL = #{ email },PHONE = #{ phone },ADDRESS = #{ address }
				  , MBTI = #{ mbti }

WHERE user_id = #{ id }
